import { useState } from "react"
import data from './data'
import './style.css'

const Accordian = () => {
  const [selection, setSelection] = useState(null)
  function handleSingleSelection(getCurrentId) {
    setSelection(getCurrentId === selection ? null : getCurrentId)
  }
  return (
    <div className="wrapper">
      <div className="accordian">
        {
          data && data.length > 0 ? (
            data.map((dataValue) => {
              return <div className="item" key={dataValue.id}>
                <div className="title"
                  onClick={() => handleSingleSelection(dataValue.id)}>
                  <h3>{dataValue.question}</h3>
                  <span>+</span>
                </div>
                <div className="acc-content">
                  {
                    selection === dataValue.id ? <div className="answer">{dataValue.answer}</div> : ""
                  }
                </div>
              </div>
            })
          ) : (<h3>Nothing found</h3>)
        }
      </div>
    </div>
  )
}

export default Accordian
